mapper.update:
UPDATE film_category
	SET category_id = #{categoryId}
WHERE film_id = #{filmId}

mapper.insert:
INSERT INTO film_category (
	film_id,
	category_id
) VALUES(
	#{filmId},
	#{categoryId}
)


	SELECT LAST_INSERT_ID()

INSERT INTO film (
	title, description, release_year, language_id, original_language_id,
	rental_duration, rental_rate, length, replacement_cost, rating,
	special_features			
) VALUES(
	#{title}, #{description}, #{releaseYear}, #{languageId}, #{originalLanguageId},
	#{rentalDuration}, #{rentalRate}, #{length}, #{replacementCost}, #{rating},
	#{specialFeatures}
)

mapper.select:
SELECT FID, title, category, price, length, rating FROM film_list
AND category = #{categoryName}
AND title LIKE CONCAT('%',#{title},'%')
AND price = #{price}
AND rating = #{rating}
AND actors LIKE CONCAT('%',#{actors},'%')
ORDER BY FID ASC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM film_list


		AND category = #{categoryName}


		AND title LIKE CONCAT('%',#{title},'%')


		AND price = #{price}


		AND rating = #{rating}


		AND actors LIKE CONCAT('%',#{actors},'%')
